
public class ForLoopFourth {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int a=0; a<=4; a++)
		{ 
							
				System.out.print("* ");
			for(int b=0; b<=4-a; b++)
				{
				System.out.print("j");
				}
			System.out.println();
		}
		

	}



	}


