
public class TableNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=1, result;
		for(int i=1; i<=12; i++)
		{
		   for(int j=1; j<=10; j++)
		     {
			   result=a*j;
					System.out.print(result+ " ");
					//a++;
		}
		  a++;
		  System.out.println();

	}

}
}
